mapper.select:
select * from j2ee.user
where user.username = #{username}
and user.password = #{password}

mapper.insert:
insert into j2ee.user(username, password, role, tel, email, addr, create_date, end_date) values(
#{u.username},#{u.password},#{u.role},#{u.tel},#{u.email},#{u.addr},#{u.create_date},#{u.end_date}
)
